import React, { useEffect, useState } from 'react'
import queryString from 'query-string'
import io from 'socket.io-client'

import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBCardHeader} from 'mdbreact';

import InfoBar from '../InfoBar/InfoBar';
import Input from '../Input/Input'


import '../Join/Join.css'
let socket;

const Chat = ({ location }) => {
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [message, setMessage] = useState([]);
    const [messages, setMessages] = useState([]);

    const ENDPOINT = 'localhost:4000';

    useEffect(() => {
        const { name, room } = queryString.parse(location.search);

        socket = io(ENDPOINT);

        setName(name);
        setRoom(room);

        socket.emit('Joined', { name, room }, () => {

        });

        return () => {
            socket.emit('disconnect');

            socket.off();
        }
    }, [ENDPOINT, location.search]);

    useEffect(() => {
        socket.on('message', (message) => {
            setMessages([...messages, message]);
        })
    }, [messages]);

    const sendMessage = (event) => {
        event.preventDefault();

        if (message) {
            socket.emit('sendMessage', message, () => setMessage(''));
        }
    }

    

    return (
        <div className="bg">
            <MDBContainer>
                <MDBRow className="d-flex justify-content-center">
                    <MDBCol sm="12" md="6" lg="4" xl="4">
                        <MDBCard className="bgbox my-5">
                            <div className=" cardy">
                                <MDBCardHeader className="teal darken-4 rounded-0 border-0 w-100 text-white">

                                    <div className="d-flex float-right">
                                        <a><i className="fas fa-video"></i></a>

                                        <a> <i className="fas fa-phone ml-3"></i></a>
                                    </div>

                                    <div className="d-flex" >

                                        <a href="/" className="small mr-3 text-white"><i className="fas fa-arrow-left"></i></a>

                                        <i className="fas fa-comments mr-2"></i> {name} 
                                        <div >{room}</div>
                                    </div>



                                </MDBCardHeader>

                                <MDBCardBody className="mx-4 my-0">
                                    <h1 className="text-white">hello</h1>

                                </MDBCardBody>
                            </div>
                        </MDBCard>
                    </MDBCol>
                </MDBRow>
                {/* <InfoBar name={<h6>{name}  <span className="small d-flex"> {room} </span> </h6>}  /> */}
                <input message={message} setMessage={setMessage} sendMessage={setMessage} />
            </MDBContainer>
        </div>
    )
}

export default Chat;
