import React, { useEffect, useState } from 'react'
import queryString from 'query-string'
import io from 'socket.io-client'

import { MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBInput, MDBBtn, MDBIcon, MDBModalFooter, MDBCardHeader } from 'mdbreact';

import '../Join/Join.css'
let socket;

const Chat = ({ location }) => {
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [message, setMessage] = useState([]);
    const [messages, setMessages] = useState([]);

    const ENDPOINT = 'localhost:4000';

    useEffect(() => {
        const { name, room } = queryString.parse(location.search);

        socket = io(ENDPOINT);

        setName(name);
        setRoom(room);

        socket.emit('Joined', { name, room }, () => {
            
        });

        return () => {
            socket.emit('disconnect');

            socket.off();
        }
    }, [ENDPOINT, location.search]);

    useEffect(() => {
        socket.on('message', (message) => {
            setMessages([...messages, message]);
        })
    }, [messages]);

    const sendMessage = (event) => {
        event.preventDefault();

        if(message){
            socket.emit('sendMessage', message, () => setMessage(''));
        }
    }

        return (
            <div className="bg">
                <MDBContainer>
                    <MDBRow className="d-flex justify-content-center">
                        <MDBCol sm="12" md="6" lg="4" xl="4">
                            <MDBCard className="my-5">
                                <div className="d-flex justify-content-center">
                                    <MDBCardHeader className="morpheus-den-gradient rounded-0 border-0 shadow w-100 text-center text-white">
                                        <h3 className="my-3">
                                            <i className="fas fa-comments "></i> Login:
                </h3>
                                    </MDBCardHeader>
                                </div>
                                <MDBCardBody className="mx-4 my-0">
                                    <h1>hello</h1>
                                </MDBCardBody>
                            </MDBCard>
                        </MDBCol>
                    </MDBRow>
                </MDBContainer>
            </div>
        )
}

export default Chat;
